import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { User, Code, Coffee, Target, Award, Briefcase, Lightbulb, GitBranch } from 'lucide-react';
import { useInView } from 'react-intersection-observer';
import { Tooltip } from 'react-tooltip';
import 'react-tooltip/dist/react-tooltip.css';

const About = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const [activeTab, setActiveTab] = useState('skills');

  const skills = {
    'Frontend Development': [
      { name: 'React', icon: '‚öõÔ∏è' },
      { name: 'JavaScript', icon: 'üìú' },
      { name: 'HTML5/CSS3', icon: 'üé®' },
      { name: 'Tailwind CSS', icon: 'üí®' },
      { name: 'Responsive Design', icon: 'üì±' }
    ],
    'Backend Development': [
      { name: 'Node.js', icon: 'üü¢' },
      { name: 'Express', icon: 'üöÄ' },
      { name: 'RESTful APIs', icon: 'üîå' },
      { name: 'MongoDB', icon: 'üçÉ' },
      { name: 'SQL', icon: 'üóÑÔ∏è' }
    ],
    'Tools & Technologies': [
      { name: 'Git & GitHub', icon: 'üîÄ' },
      { name: 'VS Code', icon: 'üíª' },
      { name: 'Figma', icon: '‚úèÔ∏è' },
      { name: 'Docker', icon: 'üê≥' },
      { name: 'Postman', icon: 'üì¨' }
    ]
  };


  const experiences = [
    {
      role: 'Full Stack Developer',
      company: 'Self-Employed',
      period: '2023 - Present',
      description: 'Building personal projects and contributing to open source while continuously learning new technologies.',
      icon: <Briefcase className="w-5 h-5" />
    },
    {
      role: 'Coding Bootcamp',
      company: 'Self-Taught',
      period: '2022 - 2023',
      description: 'Completed various online courses and built projects to master full-stack development.',
      icon: <Award className="w-5 h-5" />
    }
