import React from "react";
import { motion } from "framer-motion";
import { useInView } from "react-intersection-observer";

const Skills = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  // Define skills in card format
  const skills = [
    {
      name: "Java",
      description: "Built robust systems with OOP principles & exception handling",
      icon: "笘表

from-orange-500 to-red-500",
    },
    {
      name: "Spring Boot",
      description: "REST APIs, Dependency Injection, JPA, Microservices",
      icon: "沍ｱ",
      color: "from-green-500 to-emerald-500",
    },
    {
      name: "React.js",
      description: "SPA development with hooks, state mgmt, and component architecture",
      icon: "笞幢ｸ十

from-blue-500 to-cyan-500",
    },
    {
      name: "JavaScript",
      description: "DOM manipulation, ES6+, async programming, fetch APIs",
      icon: "沒彌

from-yellow-500 to-orange-500",
    },
    {
      name: "Tailwind CSS",
      description: "Utility-first styling for responsive and clean UIs",
      icon: "沁ｨ",
      color: "from-cyan-500 to-blue-500",
    },
  ];

  // (Reverted) Using simple per-card entrance animation without variants

  return (
    <section
      id="skills"
      className="section-padding relative overflow-hidden bg-gradient-to-b from-white via-gray-50 to-gray-100 dark:from-dark-900 dark:via-dark-800 dark:to-dark-900"
    >
      <div className="container-custom relative z-10">
        {/* Heading */}
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-dark-900 dark:text-white mb-6">
            Skills
          </h2>
          <p className="text-lg text-dark-600 dark:text-dark-400 max-w-2xl mx-auto">
            Core competencies and technical expertise for building modern web applications
          </p>
        </motion.div>

        {/* Skills Grid (Card Layout) */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-8">
          {skills.map((skill, index) => (
            <motion.div
              key={skill.name}
              initial={{ opacity: 0, y: 30 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.6, delay: index * 0.15 }}
              whileHover={{ y: -5 }}
              className="relative"
            >
              <div
                className="relative bg-white dark:bg-dark-700 rounded-2xl shadow-md p-6 text-center hover:shadow-xl transition-all duration-300 will-change-transform overflow-hidden"
                onMouseMove={(e) => {
                  const el = e.currentTarget;
                  const rect = el.getBoundingClientRect();
                  const x = e.clientX - rect.left;
                  const y = e.clientY - rect.top;
                  el.style.setProperty('--x', `${x}px`);
                  el.style.setProperty('--y', `${y}px`);
                  const midX = rect.width / 2;
                  const midY = rect.height / 2;
                  const rotateY = ((x - midX) / midX) * 8;
                  const rotateX = ((midY - y) / midY) * 8;
                  el.style.transform = `rotateX(${rotateX}deg) rotateY(${rotateY}deg)`;
                }}
                onMouseLeave={(e) => {
                  const el = e.currentTarget;
                  el.style.transform = 'rotateX(0deg) rotateY(0deg)';
                }}
                style={{ transformStyle: 'preserve-3d' }}
              >
                <div
                  className="pointer-events-none absolute -inset-8 rounded-2xl"
                  style={{
                    background:
                      'radial-gradient(400px circle at var(--x, 50%) var(--y, 50%), rgba(255,255,255,0.14), transparent 40%)',
                  }}
                  aria-hidden
                />

                {/* Icon */}
                <div
                  className={`w-16 h-16 mx-auto flex items-center justify-center rounded-xl bg-gradient-to-br ${skill.color} text-white text-3xl mb-4`}
                  style={{ transform: 'translateZ(20px)' }}
                >
                  {skill.icon}
                </div>
                {/* Skill Title */}
                <h3 className="text-lg font-semibold text-dark-900 dark:text-white mb-2" style={{ transform: 'translateZ(12px)' }}>
                  {skill.name}
                </h3>
                {/* Description */}
                <p className="text-sm text-dark-600 dark:text-dark-400" style={{ transform: 'translateZ(8px)' }}>
                  {skill.description}
                </p>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
