import axios from 'axios';
import { Briefcase, Award } from 'lucide-react';

/**
 * Fetches public LinkedIn profile data using the LinkedIn Profile Scraper API
 * @param {string} profileUrl - LinkedIn profile URL (e.g., 'https://www.linkedin.com/in/username')
 * @returns {Promise<Object>} - Parsed profile data with experience and education
 */
export async function fetchLinkedInProfile(profileUrl) {
  try {
    // Use a proxy service to avoid CORS issues
    const response = await axios.get(`https://api.allorigins.win/get?url=${encodeURIComponent(profileUrl)}`);

    if (!response.data.contents) {
      throw new Error('Failed to fetch LinkedIn profile');
    }

    const parser = new DOMParser();
    const doc = parser.parseFromString(response.data.contents, 'text/html');

    // Parse experience
    const experiences = [];
    const experienceElements = doc.querySelectorAll('#experience-section ul > li');

    experienceElements.forEach(exp => {
      try {
        const titleElement = exp.querySelector('h3');
        const companyElement = exp.querySelector('h4');
        const dateElement = exp.querySelector('span.date-range');
        const descriptionElement = exp.querySelector('p.description');

        if (titleElement && companyElement) {
          experiences.push({
            role: titleElement.textContent.trim(),
            company: companyElement.textContent.trim(),
            period: dateElement ? dateElement.textContent.trim() : '',
            description: descriptionElement ? descriptionElement.textContent.trim() : '',
            icon: <Briefcase className="w-5 h-5" />
          });
        }
      } catch (e) {
        console.error('Error parsing experience:', e);
      }
    });

    // Parse education
    const education = [];
    const educationElements = doc.querySelectorAll('#education-section li');

    educationElements.forEach(edu => {
      try {
        const degreeElement = edu.querySelector('h3');
        const institutionElement = edu.querySelector('h4');
        const dateElement = edu.querySelector('span.date-range');
        const descriptionElement = edu.querySelector('p.description');

        if (degreeElement && institutionElement) {
          education.push({
            degree: degreeElement.textContent.trim(),
            institution: institutionElement.textContent.trim(),
            period: dateElement ? dateElement.textContent.trim() : '',
            description: descriptionElement ? descriptionElement.textContent.trim() : '',
            icon: <Award className="w-5 h-5" />
          });
        }
      } catch (e) {
        console.error('Error parsing education:', e);
      }
    });

    return {
      experiences: experiences.length > 0 ? experiences : null,
      education: education.length > 0 ? education : null
    };

  } catch (error) {
    console.error('Error fetching LinkedIn data:', error);
    return {
      experiences: null,
      education: null,
      error: 'Failed to fetch LinkedIn data. Please check the URL and try again.'
    };
  }
}

// Default data in case API fails
export const defaultExperiences = [
  {
    role: 'Full Stack Developer',
    company: 'Self-Employed',
    period: '2023 - Present',
    description: 'Building personal projects and contributing to open source while continuously learning new technologies.',
    icon: <Briefcase className="w-5 h-5" />
  },
  {
    role: 'Coding Bootcamp',
    company: 'Self-Taught',
    period: '2022 - 2023',
    description: 'Completed various online courses and built projects to master full-stack development.',
    icon: <Award className="w-5 h-5" />
  }
];

export const defaultEducation = [
  {
    degree: 'B.Tech in Information Technology',
    institution: 'Kalasalingam Academy of Research and Education, Tamil Nadu',
    period: '2022 - 2026',
    score: '7.88 CGPA (Till 6th Sem)',
    icon: <Award className="w-5 h-5" />
  },
  {
    degree: '12th Standard (CBSE)',
    institution: 'Pitts Modern School, Gomia',
    period: '2021 - 2022',
    icon: <Award className="w-5 h-5" />
  },
  {
    degree: '10th Standard (CBSE)',
    institution: 'Pitts Modern School, Gomia',
    period: '2019 - 2020',
    icon: <Award className="w-5 h-5" />
  }
];
