import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence, useTransform, useMotionValue, useSpring } from 'framer-motion';
import { Mail, Phone, MapPin, Send, Github, Linkedin, MessageSquare, User, Mail as MailIcon, PhoneCall, Map } from 'lucide-react';
import { useInView } from 'react-intersection-observer';

// Particle background component
const ParticlesBackground = () => {
  return (
    <div className="absolute inset-0 overflow-hidden -z-10">
      {[...Array(15)].map((_, i) => (
        <motion.div
          key={i}
          className="absolute rounded-full bg-gradient-to-r from-primary-400/20 to-cyan-400/20"
          style={{
            width: Math.random() * 300 + 100,
            height: Math.random() * 300 + 100,
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
            filter: 'blur(60px)'
          }}
          animate={{
            x: [0, (Math.random() - 0.5) * 200],
            y: [0, (Math.random() - 0.5) * 200],
            opacity: [0.3, 0.5, 0.3]
          }}
          transition={{
            duration: Math.random() * 10 + 10,
            repeat: Infinity,
            repeatType: 'reverse',
            ease: 'easeInOut'
          }}
        />
      ))}
    </div>
  );
};

// 3D Card Component
const ContactCard = ({ icon: Icon, title, value, link, color, index }) => {
  const cardRef = useRef(null);
  const x = useMotionValue(0);
  const y = useMotionValue(0);
  const springConfig = { damping: 50, stiffness: 500 };
  const xSmooth = useSpring(useTransform(x, [-100, 100], [15, -15]), springConfig);
  const ySmooth = useSpring(useTransform(y, [-100, 100], [15, -15]), springConfig);

  const handleMouseMove = (e) => {
    if (!cardRef.current) return;
    const rect = cardRef.current.getBoundingClientRect();
    x.set(e.clientX - rect.left - rect.width / 2);
    y.set(e.clientY - rect.top - rect.height / 2);
  };

  const handleMouseLeave = () => {
    x.set(0);
    y.set(0);
  };

  return (
    <motion.a
      ref={cardRef}
      href={link}
      target="_blank"
      rel="noopener noreferrer"
      className="relative block p-6 bg-white/80 dark:bg-dark-800/80 backdrop-blur-sm rounded-2xl shadow-xl hover:shadow-2xl transition-all duration-300 overflow-hidden group"
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: 0.1 * index, duration: 0.5 }}
    >
      <motion.div 
        className="absolute inset-0 -z-10 rounded-2xl"
        style={{
          background: `linear-gradient(135deg, ${color.split(' ')[0]}00, ${color.split(' ')[2]}40)`,
          transform: 'translateZ(0)',
          willChange: 'transform',
          scale: 1.1,
          rotateX: ySmooth,
          rotateY: xSmooth,
        }}
      />
      <div className={`w-12 h-12 ${color} rounded-xl flex items-center justify-center mb-4`}>
        <Icon className="h-6 w-6 text-white" />
      </div>
      <h4 className="text-lg font-semibold text-dark-900 dark:text-white mb-1">{title}</h4>
      <p className="text-dark-600 dark:text-dark-300">{value}</p>
      <div className="absolute inset-0 rounded-2xl border-2 border-transparent group-hover:border-primary-500/20 transition-all duration-300 pointer-events-none" />
    </motion.a>
  );
};

const Contact = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: '',
  });
  const [submitting, setSubmitting] = useState(false);
  const [sent, setSent] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSent(false);
    setSubmitting(true);
    try {
      // TODO: integrate your email service here (EmailJS, API, etc.)
      await new Promise((res) => setTimeout(res, 900));
      console.log('Form submitted:', formData);
      setSent(true);
      setFormData({ name: '', email: '', subject: '', message: '' });
    } catch (err) {
      console.error(err);
    } finally {
      setSubmitting(false);
    }
  };

  const contactInfo = [
    {
      icon: MailIcon,
      title: 'Email',
      value: 'barnwalshubh8002434@gmail.com',
      link: 'mailto:barnwalshubh8002434@gmail.com',
      color: 'bg-gradient-to-r from-blue-500 to-cyan-500',
    },
    {
      icon: PhoneCall,
      title: 'Phone',
      value: '+91 7479690496',
      link: 'tel:+917479690496',
      color: 'bg-gradient-to-r from-green-500 to-emerald-500',
    },
    {
      icon: Map,
      title: 'Location',
      value: 'Chennai | Remote',
      link: '#',
      color: 'bg-gradient-to-r from-purple-500 to-pink-500',
    },
  ];

  const socialLinks = [
    { icon: Github, href: 'https://github.com/TheOnlyBaddy', label: 'GitHub', color: 'hover:text-green-600 dark:hover:text-green-400' },
    { icon: Linkedin, href: 'https://www.linkedin.com/in/shubh-barnwal', label: 'LinkedIn', color: 'hover:text-blue-600 dark:hover:text-blue-400' },
    { icon: Mail, href: 'mailto:barnwalshubh8002434@gmail.com', label: 'Email', color: 'hover:text-red-600 dark:hover:text-red-400' },
  ];

  // Form field variants for animations
  const fieldVariants = {
    hidden: { opacity: 0, y: 10 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      transition: {
        delay: 0.1 * i,
        duration: 0.5
      }
    })
  };

  return (
    <section id="contact" className="relative min-h-screen py-20 overflow-hidden bg-gradient-to-br from-dark-50 to-dark-100 dark:from-dark-900 dark:to-dark-800">
      {/* Animated Background Elements */}
      <ParticlesBackground />

      {/* Decorative gradient blobs */}
      <div className="absolute inset-0 overflow-hidden opacity-30">
        <div className="absolute top-0 left-0 w-[40rem] h-[40rem] bg-gradient-to-br from-blue-500/20 to-transparent rounded-full filter blur-3xl -translate-x-1/4 -translate-y-1/4"></div>
        <div className="absolute bottom-0 right-0 w-[50rem] h-[50rem] bg-gradient-to-tl from-purple-500/20 to-transparent rounded-full filter blur-3xl translate-x-1/4 translate-y-1/4"></div>
      </div>

      <div className="container mx-auto px-4 sm:px-6 lg:px-8 relative z-10 py-12">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16 max-w-4xl mx-auto"
        >
          <motion.span 
            className="inline-block text-sm font-semibold text-primary-600 dark:text-primary-400 mb-4 tracking-wider"
            initial={{ opacity: 0, y: 20 }}
            animate={inView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            GET IN TOUCH
          </motion.span>
          <motion.h2
            className="text-4xl md:text-6xl font-bold text-dark-900 dark:text-white mb-6 bg-clip-text text-transparent bg-gradient-to-r from-primary-500 to-cyan-500"
            initial={{ opacity: 0, y: 30 }}
            animate={inView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.3 }}
          >
            Let's Work Together
          </motion.h2>
          <motion.p
            className="text-xl text-dark-600 dark:text-dark-300 max-w-3xl mx-auto leading-relaxed"
            initial={{ opacity: 0, y: 30 }}
            animate={inView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            Have a project in mind or want to chat? I'd love to hear from you!
          </motion.p>
        </motion.div>

        <div className="grid lg:grid-cols-2 gap-8 sm:gap-12 max-w-7xl mx-auto">
          {/* Left Column - Contact Form */}
          <motion.div
            className="space-y-6"
            initial={{ opacity: 0, x: -50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ delay: 0.4 }}
              className="relative p-8 bg-white/80 dark:bg-dark-800/80 backdrop-blur-sm rounded-3xl shadow-2xl overflow-hidden"
            >
              <div className="absolute top-0 left-0 w-full h-1.5 bg-gradient-to-r from-primary-400 to-cyan-500"></div>
              <h3 className="text-2xl font-bold text-dark-900 dark:text-white mb-8">
                Send Me a Message
              </h3>

              <form onSubmit={handleSubmit} className="space-y-6">
                <AnimatePresence>
                  {sent && (
                    <motion.div
                      initial={{ opacity: 0, y: -20 }}
                      animate={{ opacity: 1, y: 0 }}
                      exit={{ opacity: 0, y: -20 }}
                      className="p-4 mb-6 text-sm text-green-700 bg-green-100 dark:bg-green-900/50 dark:text-green-300 rounded-lg"
                    >
                      Message sent successfully! I'll get back to you soon.
                    </motion.div>
                  )}
                </AnimatePresence>

                <div className="space-y-6">
                  {/* Name Field */}
                  <motion.div 
                    className="relative"
                    variants={fieldVariants}
                    initial="hidden"
                    animate={inView ? "visible" : "hidden"}
                    custom={0}
                  >
                    <input
                      type="text"
                      id="name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      onFocus={() => setActiveField('name')}
                      onBlur={() => setActiveField(null)}
                      className="w-full px-4 pt-6 pb-2 rounded-xl border border-dark-200 dark:border-dark-700 bg-white/50 dark:bg-dark-700/50 text-dark-900 dark:text-white focus:ring-2 focus:ring-primary-500 focus:border-transparent transition-all duration-200 peer"
                      required
                    />
                    <label 
                      htmlFor="name" 
                      className={`absolute left-4 transition-all duration-300 ${
                        activeField === 'name' || formData.name ? 'text-xs top-2 text-primary-500' : 'text-sm top-1/2 -translate-y-1/2 text-dark-500 dark:text-dark-400'
                      }`}
                    >
                      Your Name
                    </label>
                  </motion.div>

                  {/* Email Field */}
                  <motion.div 
                    className="relative"
                    variants={fieldVariants}
                    initial="hidden"
                    animate={inView ? "visible" : "hidden"}
                    custom={1}
                  >
                    <input
                      type="email"
                      id="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      onFocus={() => setActiveField('email')}
                      onBlur={() => setActiveField(null)}
                      className="w-full px-4 pt-6 pb-2 rounded-xl border border-dark-200 dark:border-dark-700 bg-white/50 dark:bg-dark-700/50 text-dark-900 dark:text-white focus:ring-2 focus:ring-primary-500 focus:border-transparent transition-all duration-200 peer"
                      required
                    />
                    <label 
                      htmlFor="email" 
                      className={`absolute left-4 transition-all duration-300 ${
                        activeField === 'email' || formData.email ? 'text-xs top-2 text-primary-500' : 'text-sm top-1/2 -translate-y-1/2 text-dark-500 dark:text-dark-400'
                      }`}
                    >
                      Email Address
                    </label>
                  </motion.div>

                  {/* Subject Field */}
                  <motion.div 
                    className="relative"
                    variants={fieldVariants}
                    initial="hidden"
                    animate={inView ? "visible" : "hidden"}
                    custom={2}
                  >
                    <input
                      type="text"
                      id="subject"
                      name="subject"
                      value={formData.subject}
                      onChange={handleInputChange}
                      onFocus={() => setActiveField('subject')}
                      onBlur={() => setActiveField(null)}
                      className="w-full px-4 pt-6 pb-2 rounded-xl border border-dark-200 dark:border-dark-700 bg-white/50 dark:bg-dark-700/50 text-dark-900 dark:text-white focus:ring-2 focus:ring-primary-500 focus:border-transparent transition-all duration-200 peer"
                      required
                    />
                    <label 
                      htmlFor="subject" 
                      className={`absolute left-4 transition-all duration-300 ${
                        activeField === 'subject' || formData.subject ? 'text-xs top-2 text-primary-500' : 'text-sm top-1/2 -translate-y-1/2 text-dark-500 dark:text-dark-400'
                      }`}
                    >
                      Subject
                    </label>
                  </motion.div>

                  {/* Message Field */}
                  <motion.div 
                    className="relative"
                    variants={fieldVariants}
                    initial="hidden"
                    animate={inView ? "visible" : "hidden"}
                    custom={3}
                  >
                    <textarea
                      id="message"
                      name="message"
                      value={formData.message}
                      onChange={handleInputChange}
                      onFocus={() => setActiveField('message')}
                      onBlur={() => setActiveField(null)}
                      rows="5"
                      className="w-full px-4 pt-8 pb-4 rounded-xl border border-dark-200 dark:border-dark-700 bg-white/50 dark:bg-dark-700/50 text-dark-900 dark:text-white focus:ring-2 focus:ring-primary-500 focus:border-transparent transition-all duration-200 peer resize-none"
                      required
                    ></textarea>
                    <label 
                      htmlFor="message" 
                      className={`absolute left-4 transition-all duration-300 ${
                        activeField === 'message' || formData.message ? 'text-xs top-3 text-primary-500' : 'text-sm top-5 text-dark-500 dark:text-dark-400'
                      }`}
                    >
                      Your Message
                    </label>
                  </motion.div>

                  {/* Submit Button */}
                  <motion.div 
                    className="relative group mt-6"
                    variants={fieldVariants}
                    initial="hidden"
                    animate={inView ? "visible" : "hidden"}
                    custom={4}
                  >
                    <button
                      type="submit"
                      disabled={submitting}
                      className="relative w-full py-4 px-6 bg-gradient-to-r from-primary-500 to-cyan-500 text-white font-medium rounded-xl overflow-hidden group-hover:opacity-90 transition-all duration-300"
                    >
                      <div className="relative z-10 flex items-center justify-center space-x-2">
                        {submitting ? (
                          <>
                            <svg className="animate-spin h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                            </svg>
                            <span>Sending...</span>
                          </>
                        ) : (
                          <>
                            <Send className="h-5 w-5" />
                            <span>Send Message</span>
                          </>
                        )}
                      </div>
                      <div className="absolute inset-0 bg-gradient-to-r from-primary-600 to-cyan-600 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                    </button>
                    <div className="absolute -inset-1 bg-gradient-to-r from-primary-500 to-cyan-500 rounded-xl blur opacity-20 group-hover:opacity-30 transition-all duration-300 -z-10"></div>
                  </motion.div>
                </div>
              </form>
            </motion.div>
          </motion.div>

          {/* Right Column - Contact Info & Social */}
          <motion.div
            className="space-y-8"
            initial={{ opacity: 0, x: 50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            <div>
              <h3 className="text-2xl font-semibold text-dark-900 dark:text-white mb-6">
                Contact Information
              </h3>
              <div className="grid gap-6">
                {contactInfo.map((info, index) => (
                  <ContactCard
                    key={info.title}
                    icon={info.icon}
                    title={info.title}
                    value={info.value}
                    link={info.link}
                    color={info.color}
                    index={index}
                  />
                ))}
              </div>
            </div>

            {/* Social Links */}
            <div>
              <h3 className="text-2xl font-semibold text-dark-900 dark:text-white mb-6">
                Follow Me
              </h3>
              <div className="flex space-x-4">
                {socialLinks.map((social, index) => (
                  <motion.a
                    key={social.label}
                    href={social.href}
                    className={`p-4 bg-white dark:bg-dark-700 rounded-lg hover:shadow-lg transition-all duration-300 ${social.color}`}
                    whileHover={{ scale: 1.1, y: -5 }}
                    whileTap={{ scale: 0.95 }}
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={inView ? { opacity: 1, scale: 1 } : {}}
                    transition={{ duration: 0.6, delay: 1.4 + index * 0.1 }}
                  >
                    <social.icon className="h-6 w-6" />
                  </motion.a>
                ))}
              </div>
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
