// src/components/Projects.js
import React, { useEffect, useState, useContext, useRef } from "react";
import { motion, AnimatePresence, useInView } from "framer-motion";
import { FiGithub, FiExternalLink, FiStar, FiGitBranch } from "react-icons/fi";
import ThemeContext from "../context/ThemeContext";

// Custom styles for the component
const customStyles = `
  @keyframes float {
    0% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
    100% { transform: translateY(0px); }
  }

  .project-card {
    transition: all 0.3s ease;
    transform-style: preserve-3d;
  }

  .tech-tag {
    @apply px-3 py-1 rounded-full text-xs font-medium transition-all duration-300;
    background: rgba(99, 102, 241, 0.1);
    color: #6366f1;
  }

  .skeleton {
    @apply bg-gray-200 dark:bg-gray-700 animate-pulse rounded-md;
  }
`;

// Add this style tag to your component
const StyleTag = () => (
  <style dangerouslySetInnerHTML={{ __html: customStyles }} />
);

// Function to convert language name to CSS class name
const getLanguageClassName = (lang) => {
  if (!lang) return 'tech-tag-other';

  // Map of language names to their corresponding CSS class names
  const languageMap = {
    // Popular languages
    'JavaScript': 'javascript',
    'TypeScript': 'typescript',
    'Python': 'python',
    'Java': 'java',
    'HTML': 'html',
    'CSS': 'css',
    'PHP': 'php',
    'Ruby': 'ruby',
    'C++': 'cpp',
    'C#': 'csharp',
    'Go': 'go',
    'Rust': 'rust',
    'Swift': 'swift',
    'Kotlin': 'kotlin',
    'Dart': 'dart',
    'Shell': 'shell',
    'PowerShell': 'powershell',

    // Frameworks
    'Vue': 'vue',
    'React': 'react',
    'Angular': 'angular',
    'Svelte': 'svelte',

    // Other
    'Dockerfile': 'dockerfile',
    'Makefile': 'makefile',
    'Jupyter Notebook': 'jupyter',
    'Jupyter': 'jupyter',
    'SCSS': 'css',
    'Sass': 'css',
    'Less': 'css',
    'TypeScript React': 'typescript',
    'TSX': 'typescript',
    'JSX': 'javascript',
    'Markdown': 'markdown',
    'JSON': 'json',
    'YAML': 'yaml',
    'GraphQL': 'graphql',
    'SQL': 'sql',
    'PLSQL': 'sql',
    'PLpgSQL': 'sql'
  };

  // Find the language in our map, or use a default
  const className = languageMap[lang] || lang.toLowerCase().replace(/[^a-z0-9]/g, '');
  return `tech-tag-${className}`;
};

const Projects = () => {
  const [repos, setRepos] = useState([]);
  const [languages, setLanguages] = useState({});
  const { isDark } = useContext(ThemeContext);

  // Project images mapping - add your project images to the public/images/projects directory
  const projectImages = {
    'project1': '/images/projects/project1.jpg',
    'project2': '/images/projects/project2.jpg',
    // Add more project images as needed
  };

  // Function to fetch languages for a repository
  const fetchLanguages = async (repoName) => {
    try {
      const response = await fetch(`https://api.github.com/repos/TheOnlyBaddy/${repoName}/languages`);
      if (!response.ok) return [];
      const data = await response.json();
      return Object.keys(data);
    } catch (error) {
      console.error(`Error fetching languages for ${repoName}:`, error);
      return [];
    }
  };

  useEffect(() => {
    const fetchRepos = async () => {
      try {
        const response = await fetch("https://api.github.com/users/TheOnlyBaddy/repos");
        if (!response.ok) {
          throw new Error(`GitHub API error: ${response.status}`);
        }
        const data = await response.json();
        const filtered = Array.isArray(data) 
          ? data.filter(repo => !repo.fork && !repo.private)
          : [];

        setRepos(filtered);

        // Fetch languages for each repository
        const languagesData = {};
        for (const repo of filtered) {
          const repoLanguages = await fetchLanguages(repo.name);
          languagesData[repo.name] = repoLanguages;
        }
        setLanguages(languagesData);
      } catch (err) {
        console.error(err);
        setRepos([]);
      }
    };

    fetchRepos();
  }, []);

  return (
    <section id="projects"
      className={`py-16 transition-colors duration-500 ${
        isDark ? "bg-gray-900 text-white" : "bg-gray-50 text-gray-900"
      }`}
    >
      <div className="container mx-auto px-4 sm:px-6">
        <motion.h2 
          className="text-4xl font-bold mb-12 text-center"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          My <span className="text-primary-600 dark:text-primary-400">Projects</span>
        </motion.h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {repos.map((repo, index) => (
            <motion.div
              key={repo.id}
              className={`rounded-xl overflow-hidden shadow-lg transform transition-all duration-300 hover:shadow-2xl ${
                isDark ? "bg-gray-800 text-gray-200" : "bg-white text-gray-900"
              }`}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              whileHover={{ y: -5 }}
            >
              {/* Project Image */}
              <div className="h-48 overflow-hidden bg-gray-100 dark:bg-gray-700">
                <img 
                  src={projectImages[repo.name] || '/images/projects/default.jpg'} 
                  alt={repo.name}
                  className="w-full h-full object-cover transform hover:scale-105 transition-transform duration-500"
                  onError={(e) => {
                    e.target.src = '/images/projects/default.jpg';
                  }}
                />
              </div>
              <div className="p-6">
                <h3 className="text-xl font-bold mb-3 text-gray-900 dark:text-white">{repo.name}</h3>
              <div className="text-sm mb-5 text-gray-600 dark:text-gray-300">
                {repo.description ? (
                  <p className="line-clamp-3 mb-4">{repo.description}</p>
                ) : (
                  <p className="text-gray-500 italic">No description provided</p>
                )}

                {/* Technology Tags */}
                {languages[repo.name]?.length > 0 && (
                  <div className="mt-4">
                    <h4 className="text-sm font-medium mb-2 text-gray-700 dark:text-gray-300">
                      Technologies Used: 
                    </h4>
                    <div className="flex flex-wrap gap-2">
                      {languages[repo.name].map((lang) => {
                        const languageClass = getLanguageClassName(lang);
                        return (
                          <span 
                            key={lang}
                            className={`tech-tag ${languageClass}`}
                            title={lang}
                          >
                            {lang}
                          </span>
                        );
                      })}
                    </div>
                  </div>
                )}
              </div>
              <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-700 flex justify-between items-center">
                <a
                  href={repo.html_url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="inline-flex items-center text-sm font-medium text-primary-600 hover:text-primary-700 dark:text-primary-400 dark:hover:text-primary-300 transition-colors"
                >
                  View on GitHub
                  <svg className="w-4 h-4 ml-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                  </svg>
                </a>
                {repo.homepage && (
                  <a
                    href={repo.homepage}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-sm font-medium text-gray-600 hover:text-gray-900 dark:text-gray-400 dark:hover:text-white transition-colors"
                  >
                    Live Demo
                  </a>
                )}
              </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
