// src/components/Projects.js
import React, { useEffect, useState, useContext } from "react";
import { motion } from "framer-motion";
import ThemeContext from "../context/ThemeContext";

const Projects = () => {
  const [repos, setRepos] = useState([]);
  const { isDark } = useContext(ThemeContext);

  const containerVariants = {
    hidden: {},
    show: {
      transition: { staggerChildren: 0.08, delayChildren: 0.15 },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20, scale: 0.98 },
    show: {
      opacity: 1,
      y: 0,
      scale: 1,
      transition: { type: "spring", stiffness: 130, damping: 18 },
    },
  };

  const languageGradient = {
    JavaScript: "from-yellow-400/40 to-orange-400/30",
    TypeScript: "from-sky-400/40 to-blue-400/30",
    Java: "from-orange-500/40 to-red-400/30",
    Python: "from-blue-400/40 to-green-400/30",
    CSS: "from-sky-400/40 to-cyan-300/30",
    HTML: "from-orange-400/40 to-pink-400/30",
    Go: "from-cyan-400/40 to-blue-400/30",
    Ruby: "from-rose-400/40 to-pink-400/30",
    PHP: "from-indigo-400/40 to-violet-400/30",
    "C++": "from-blue-500/40 to-indigo-400/30",
    C: "from-slate-400/40 to-slate-300/30",
    "C#": "from-purple-500/40 to-indigo-400/30",
    Kotlin: "from-violet-500/40 to-fuchsia-400/30",
    Swift: "from-orange-500/40 to-amber-400/30",
    Rust: "from-amber-600/40 to-orange-500/30",
    default: "from-blue-400/30 to-purple-400/20",
  };

  const getGradientForLanguage = (language) => {
    if (!language) return languageGradient.default;
    return languageGradient[language] || languageGradient.default;
  };

  useEffect(() => {
    fetch("https://api.github.com/users/TheONlyBaddy/repos")
      .then((res) => {
        if (!res.ok) {
          throw new Error(`GitHub API error: ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        const list = Array.isArray(data) ? data : [];
        const filtered = list.filter((repo) => !repo.fork && !repo.private);
        setRepos(filtered);
      })
      .catch((err) => {
        console.error(err);
        setRepos([]);
      });
  }, []);

  return (
    <section id="projects"
      className={`py-10 transition-colors duration-500 ${
        isDark ? "bg-gray-900 text-white" : "bg-gray-100 text-gray-900"
      }`}
    >
      <div className="container mx-auto px-6">
        <h2 className="text-3xl font-bold mb-6 text-center">My Projects</h2>
        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6"
          variants={containerVariants}
          initial="hidden"
          animate="show"
        >
          {repos.map((repo) => (
            <motion.div
              key={repo.id}
              className={`relative overflow-hidden p-5 rounded-2xl shadow-md transform transition ${
                isDark ? "bg-gray-800 text-gray-200" : "bg-white text-gray-900"
              }`}
              variants={itemVariants}
              whileHover={{ y: -6, scale: 1.02, rotate: 0.15, rotateX: -3, rotateY: 3 }}
              whileTap={{ scale: 0.98 }}
              style={{ transformPerspective: 800 }}
            >
              <motion.div
                className={`pointer-events-none absolute -inset-10 bg-gradient-to-br ${getGradientForLanguage(
                  repo.language
                )} blur-2xl`}
                aria-hidden
                initial={{ opacity: 0.35 }}
                animate={{ opacity: [0.35, 0.55, 0.35] }}
                transition={{ duration: 3, repeat: Infinity, ease: "easeInOut" }}
              />
              <div className="relative">
                <h3 className="text-xl font-semibold mb-2">{repo.name}</h3>
                <p className="text-sm mb-4">
                  {repo.description ? repo.description : "No description"}
                </p>
                <a
                  href={repo.html_url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-500 hover:underline"
                >
                  View on GitHub â†’
                </a>
              </div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default Projects;
