import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useInView } from "react-intersection-observer";

const Skills = () => {
  const [activeCategory, setActiveCategory] = useState("all");
  const [ref, inView] = useInView({ triggerOnce: true, threshold: 0.1 });

  const categories = [
    { id: "all", name: "All Skills", slug: "all" },
    { id: "frontend", name: "Frontend", slug: "frontend" },
    { id: "backend", name: "Backend", slug: "backend" },
    { id: "tools", name: "Tools", slug: "tools" },
  ];

  const skills = [
    {
      name: "React.js",
      description: "Building interactive UIs with hooks and context",
      icon: "âš›ï¸",
      color: "from-blue-500 to-cyan-400",
      category: ["frontend"],
    },
    {
      name: "JavaScript",
      description: "ES6+, async/await, functional programming",
      icon: "ðŸ“œ",
      color: "from-yellow-400 to-orange-500",
      category: ["frontend", "backend"],
    },
    {
      name: "Node.js",
      description: "Server-side JavaScript and RESTful APIs",
      icon: "ðŸŸ¢",
      color: "from-green-600 to-emerald-400",
      category: ["backend"],
    },
    {
      name: "Python",
      description: "Scripting, automation, and web development",
      icon: "ðŸ",
      color: "from-blue-600 to-cyan-400",
      category: ["backend"],
    },
    {
      name: "Tailwind CSS",
      description: "Utility-first CSS framework",
      icon: "ðŸŽ¨",
      color: "from-cyan-400 to-blue-500",
      category: ["frontend"],
    },
    {
      name: "Git & GitHub",
      description: "Version control and collaboration",
      icon: "ðŸ”€",
      color: "from-gray-700 to-gray-500",
      category: ["tools"],
    },
    {
      name: "Docker",
      description: "Containerization and deployment",
      icon: "ðŸ³",
      color: "from-blue-400 to-cyan-500",
      category: ["tools"],
    },
    {
      name: "Next.js",
      description: "React framework for production",
      icon: "â­ï¸",
      color: "from-black to-gray-700",
      category: ["frontend"],
    },
    {
      name: "MongoDB",
      description: "NoSQL database",
      icon: "ðŸƒ",
      color: "from-green-500 to-emerald-400",
      category: ["backend", "tools"],
    },
  ];

  const filteredSkills = React.useMemo(() => {
    if (activeCategory === "all") {
      return skills;
    }
    return skills.filter(skill => 
      skill.category.includes(activeCategory)
    );
  }, [activeCategory, skills]);

  return (
    <section id="skills" className="py-20 bg-gradient-to-b from-white to-gray-50 dark:from-dark-900 dark:to-dark-800">
      <div className="container-custom px-4 sm:px-6">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          className="text-center mb-12"
        >
          <h2 className="text-4xl font-bold text-dark-900 dark:text-white mb-4">
            My <span className="text-primary-500">Skills</span>
          </h2>
          <p className="text-lg text-dark-600 dark:text-dark-300">
            Technologies I work with to create amazing digital experiences
          </p>
        </motion.div>

        <div className="flex flex-wrap justify-center gap-3 mb-8">
          {categories.map((category) => (
            <button
              key={category.id}
              type="button"
              onClick={() => setActiveCategory(category.slug)}
              className={`px-4 py-2 rounded-full text-sm font-medium transition-all duration-200 ${
                activeCategory === category.slug
                  ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-md transform scale-105'
                  : 'bg-white dark:bg-dark-700 text-dark-600 dark:text-dark-300 hover:bg-gray-100 dark:hover:bg-dark-600 hover:shadow-md'
              }`}
            >
              {category.name}
            </button>
          ))}
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          <AnimatePresence>
            {filteredSkills.map((skill, index) => (
              <motion.div
                key={skill.name}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3, delay: index * 0.1 }}
                className="bg-white dark:bg-dark-700 rounded-xl p-6 shadow-sm hover:shadow-lg transition-shadow duration-300"
              >
                <div className="flex items-start space-x-4">
                  <div className={`p-3 rounded-lg bg-gradient-to-br ${skill.color} text-white`}>
                    <span className="text-2xl">{skill.icon}</span>
                  </div>
                  <div className="flex-1">
                    <h3 className="text-lg font-semibold text-dark-900 dark:text-white">
                      {skill.name}
                    </h3>
                    <p className="text-sm text-dark-600 dark:text-dark-400 mt-1">
                      {skill.description}
                    </p>
                  </div>
                </div>
              </motion.div>
            ))}
          </AnimatePresence>
        </div>
      </div>
    </section>
  );
};

export default Skills;
