import React, { useEffect, useState } from "react";

const Projects = () => {
  const [repos, setRepos] = useState([]);

  useEffect(() => {
    fetch("https://api.github.com/users/TheOnlyBaddy/repos")
      .then((res) => res.json())
      .then((data) => {
        const filtered = data.filter(repo => !repo.fork);
        setRepos(filtered);
      })
      .catch((err) => console.error("Error fetching repos:", err));
  }, []);

  return (
    <section id="projects" className="py-20 bg-gradient-to-b from-gray-900 to-gray-800">
      <div className="max-w-6xl mx-auto px-6">
        {/* Heading */}
        <h2 className="text-4xl font-bold text-center text-white mb-12">
          Featured <span className="text-blue-400">Projects</span>
        </h2>

        {repos.length === 0 ? (
          <p className="text-center text-gray-400">Loading projects...</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10">
            {repos.map((repo) => (
              <div
                key={repo.id}
                className="bg-gray-800 rounded-2xl shadow-lg overflow-hidden hover:shadow-2xl transition duration-300"
              >
                {/* Project Image Placeholder (you can replace with repo-specific image) */}
                <div className="h-40 bg-gray-700 flex items-center justify-center text-gray-500 text-sm">
                  Project Image
                </div>

                {/* Project Content */}
                <div className="p-6">
                  <h3 className="text-xl font-semibold text-white">{repo.name}</h3>
                  <p className="text-gray-400 mt-2">
                    {repo.description || "No description available"}
                  </p>

                  {/* Tags (use GitHub topics if available) */}
                  <div className="flex flex-wrap gap-2 mt-4">
                    {repo.topics && repo.topics.length > 0 ? (
                      repo.topics.map((topic) => (
                        <span
                          key={topic}
                          className="px-3 py-1 text-sm rounded-full bg-gray-700 text-gray-300"
                        >
                          {topic}
                        </span>
                      ))
                    ) : (
                      <span className="px-3 py-1 text-sm rounded-full bg-gray-700 text-gray-400">
                        No tags
                      </span>
                    )}
                  </div>

                  {/* Buttons */}
                  <div className="flex justify-between items-center mt-6">
                    <a
                      href={`https://TheOnlyBddy.github.io/${repo.name}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="px-4 py-2 rounded-lg bg-blue-600 text-white hover:bg-blue-500"
                    >
                      Live Demo
                    </a>
                    <a
                      href={repo.html_url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="px-4 py-2 rounded-lg bg-gray-700 text-gray-300 hover:bg-gray-600"
                    >
                      Code
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </section>
  );
};

export default Projects;
