import React from 'react';
import { motion } from 'framer-motion';
import { User, Code, Coffee, Target } from 'lucide-react';
import { useInView } from 'react-intersection-observer';

const About = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const features = [
    {
      icon: Code,
      title: 'Clean Code',
      description: 'Writing maintainable, readable, and efficient code that scales.',
      color: 'from-blue-500 to-cyan-500',
    },
    {
      icon: Target,
      title: 'Problem Solver',
      description: 'Breaking down complex problems into elegant, simple solutions.',
      color: 'from-purple-500 to-pink-500',
    },
    {
      icon: Coffee,
      title: 'Continuous Learning',
      description: 'Always exploring new technologies and best practices.',
      color: 'from-orange-500 to-red-500',
    },
    {
      icon: User,
      title: 'Team Player',
      description: 'Collaborating effectively in agile development environments.',
      color: 'from-green-500 to-teal-500',
    },
  ];

  return (
    <section id="about" className="section-padding relative overflow-hidden section-divider">
      {/* Background with subtle blur */}
      <div className="absolute inset-0 bg-gradient-to-br from-gray-50/80 via-white/90 to-gray-100/80 dark:from-dark-800/80 dark:via-dark-700/90 dark:to-dark-800/80 backdrop-blur-[2px]"></div>
      
      {/* Decorative background elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-10 right-10 w-64 h-64 bg-gradient-to-r from-green-400/10 to-blue-400/10 rounded-full blur-2xl float-animation"></div>
        <div className="absolute bottom-10 left-10 w-80 h-80 bg-gradient-to-r from-orange-400/10 to-red-400/10 rounded-full blur-2xl float-animation" style={{animationDelay: '3s'}}></div>
        <div className="absolute top-1/2 left-20 w-32 h-32 bg-gradient-to-r from-yellow-400/8 to-orange-400/8 rounded-full blur-xl float-animation" style={{animationDelay: '1.5s'}}></div>
      </div>

      <div className="container-custom relative z-10">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          {/* Profile Photo Section */}
          <motion.div
            className="flex justify-center mb-8"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={inView ? { opacity: 1, scale: 1 } : {}}
            transition={{ duration: 0.6, delay: 0.3 }}
          >
            <div className="relative">
              {/* Profile Photo Container */}
              <div className="w-32 h-32 md:w-40 md:h-40 rounded-full overflow-hidden border-4 border-white dark:border-dark-700 shadow-lg relative group profile-photo-container">
                {/* Profile Photo - Replace the src with your actual photo path */}
                <img 
                  src="/pfp.jpg" 
                  alt="Profile Photo"
                  className="w-full h-full object-cover"
                  onError={(e) => {
                    // Fallback to initials if image fails to load
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'flex';
                  }}
                />
                
                {/* Fallback Initials - Displayed if no image or image fails to load */}
                <div className="w-full h-full bg-gradient-to-br from-primary-400 to-primary-600 flex items-center justify-center text-white text-4xl md:text-5xl font-bold hidden">
                  YN
                </div>
                
                {/* Decorative Ring */}
                <div className="absolute inset-0 rounded-full border-4 border-transparent bg-gradient-to-r from-primary-400 to-primary-600 p-1 -z-10 profile-photo-ring"></div>
              </div>
            </div>
          </motion.div>

          <motion.h2
            className="text-4xl md:text-5xl font-bold text-dark-900 dark:text-white mb-6"
            initial={{ opacity: 0, y: 30 }}
            animate={inView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            About <span className="gradient-text">Me</span>
          </motion.h2>
          <motion.p
            className="text-lg text-dark-600 dark:text-dark-400 max-w-3xl mx-auto"
            initial={{ opacity: 0, y: 30 }}
            animate={inView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            A passionate developer who loves turning ideas into reality through code.
          </motion.p>
        </motion.div>

        <div className="grid md:grid-cols-2 gap-8 sm:gap-12 items-center">
          {/* Left Column - Text Content */}
          <motion.div
            className="space-y-6"
            initial={{ opacity: 0, x: -50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            <h3 className="text-2xl md:text-3xl font-semibold text-dark-900 dark:text-white">
              Turning Vision Into Reality
            </h3>
            <p className="text-lg text-dark-600 dark:text-dark-400 leading-relaxed">
              I'm a full-stack developer with a passion for creating exceptional digital experiences. 
              With expertise in modern web technologies, I specialize in building scalable applications 
              that solve real-world problems.
            </p>
            <p className="text-lg text-dark-600 dark:text-dark-400 leading-relaxed">
              My journey in software development started with curiosity and has evolved into a 
              commitment to writing clean, maintainable code. I believe in the power of technology 
              to transform businesses and improve lives.
            </p>
            
            {/* Stats */}
            <div className="grid grid-cols-2 gap-6 pt-6">
              <motion.div
                className="text-center p-4 bg-white dark:bg-dark-700 rounded-lg shadow-sm"
                whileHover={{ scale: 1.05, y: -5 }}
                transition={{ duration: 0.3 }}
              >
                <div className="text-3xl font-bold gradient-text">Fresher Developer</div>
                <div className="text-sm text-dark-600 dark:text-dark-400">Actively learning & growing</div>
              </motion.div>
              <motion.div
                className="text-center p-4 bg-white dark:bg-dark-700 rounded-lg shadow-sm"
                whileHover={{ scale: 1.05, y: -5 }}
                transition={{ duration: 0.3 }}
              >
                <div className="text-3xl font-bold gradient-text">Practical Projects</div>
                <div className="text-sm text-dark-600 dark:text-dark-400">Built with latest tools</div>
              </motion.div>
            </div>
          </motion.div>

          {/* Right Column - Features Grid */}
          <motion.div
            className="grid grid-cols-2 gap-4"
            initial={{ opacity: 0, x: 50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            {features.map((feature, index) => (
              <motion.div
                key={feature.title}
                className="p-6 bg-white dark:bg-dark-700 rounded-xl shadow-sm hover:shadow-lg transition-all duration-300"
                whileHover={{ scale: 1.05, y: -5 }}
                whileTap={{ scale: 0.95 }}
                initial={{ opacity: 0, y: 30 }}
                animate={inView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.6, delay: 1.0 + index * 0.1 }}
              >
                <div className={`w-12 h-12 rounded-lg bg-gradient-to-r ${feature.color} flex items-center justify-center mb-4`}>
                  <feature.icon className="h-6 w-6 text-white" />
                </div>
                <h4 className="text-lg font-semibold text-dark-900 dark:text-white mb-2">
                  {feature.title}
                </h4>
                <p className="text-sm text-dark-600 dark:text-dark-400">
                  {feature.description}
                </p>
              </motion.div>
            ))}
          </motion.div>
        </div>

        {/* Additional Info */}
        <motion.div
          className="mt-16 text-center"
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 1.4 }}
        >
          <p className="text-lg text-dark-600 dark:text-dark-400 mb-6">
            When I'm not coding, you'll find me exploring new technologies, contributing to open source, 
            or sharing knowledge with the developer community.
          </p>
          <motion.button
            className="px-8 py-3 border-2 border-primary-600/80 text-primary-600 dark:text-primary-400 hover:bg-primary-600/90 hover:text-white font-semibold rounded-full transition-all duration-300 btn-blur-light"
            whileHover={{ scale: 1.05, y: -2 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => window.open('https://www.linkedin.com/in/shubh-barnwal', '_blank')}
          >
            Learn More
          </motion.button>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
