import React, { useEffect, useState } from "react";

const Projects = () => {
  const [repos, setRepos] = useState([]);

  useEffect(() => {
    // replace 'YOUR_GITHUB_USERNAME' with your actual GitHub username
    fetch("https://api.github.com/users/TheOnlyBaddy/repos")
      .then((res) => res.json())
      .then((data) => {
        // filter out forked repos if you only want originals
        const filtered = data.filter(repo => !repo.fork);
        setRepos(filtered);
      })
      .catch((err) => console.error("Error fetching repos:", err));
  }, []);

  return (
    <section id="projects" className="py-12 px-6">
      <h2 className="text-3xl font-bold mb-6 text-center">My Projects</h2>

      {repos.length === 0 ? (
        <p className="text-center text-gray-400">Loading projects...</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {repos.map((repo) => (
            <div
              key={repo.id}
              className="p-6 rounded-2xl shadow-lg bg-gray-800 hover:bg-gray-700 transition transform hover:scale-105"
            >
              <h3 className="text-xl font-semibold text-white">{repo.name}</h3>
              <p className="text-gray-300 mt-2">
                {repo.description || "No description available"}
              </p>
              <div className="mt-4 flex justify-between items-center">
                <span className="text-sm text-gray-400">
                  ‚≠ê {repo.stargazers_count}
                </span>
                <a
                  href={repo.html_url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="px-3 py-1 rounded-md bg-blue-600 text-white hover:bg-blue-500"
                >
                  View Repo
                </a>
              </div>
            </div>
          ))}
        </div>
      )}
    </section>
  );
};

export default Projects;
