import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const Skills = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const skillCategories = [
    {
      title: 'Frontend Development',
      skills: [
        { name: 'React', level: 90, color: 'from-blue-500 to-cyan-500' },
        { name: 'JavaScript', level: 85, color: 'from-yellow-500 to-orange-500' },
        { name: 'TypeScript', level: 80, color: 'from-blue-600 to-blue-700' },
        { name: 'HTML/CSS', level: 95, color: 'from-orange-500 to-red-500' },
        { name: 'Tailwind CSS', level: 88, color: 'from-cyan-500 to-blue-500' },
      ]
    },
    {
      title: 'Backend Development',
      skills: [
        { name: 'Node.js', level: 85, color: 'from-green-500 to-emerald-500' },
        { name: 'Express.js', level: 80, color: 'from-gray-600 to-gray-700' },
        { name: 'Python', level: 75, color: 'from-blue-500 to-purple-500' },
        { name: 'PostgreSQL', level: 70, color: 'from-blue-600 to-indigo-600' },
        { name: 'MongoDB', level: 72, color: 'from-green-600 to-green-700' },
      ]
    },
    {
      title: 'Tools & Technologies',
      skills: [
        { name: 'Git', level: 90, color: 'from-orange-600 to-red-600' },
        { name: 'Docker', level: 70, color: 'from-blue-500 to-indigo-500' },
        { name: 'AWS', level: 65, color: 'from-yellow-500 to-orange-500' },
        { name: 'Figma', level: 75, color: 'from-purple-500 to-pink-500' },
        { name: 'VS Code', level: 95, color: 'from-blue-500 to-cyan-500' },
      ]
    }
  ];

  const technologies = [
    { name: 'React', icon: '‚öõÔ∏è', color: 'bg-blue-100 dark:bg-blue-900/20' },
    { name: 'Node.js', icon: 'üü¢', color: 'bg-green-100 dark:bg-green-900/20' },
    { name: 'TypeScript', icon: 'üî∑', color: 'bg-blue-100 dark:bg-blue-900/20' },
    { name: 'Tailwind', icon: 'üé®', color: 'bg-cyan-100 dark:bg-cyan-900/20' },
    { name: 'MongoDB', icon: 'üçÉ', color: 'bg-green-100 dark:bg-green-900/20' },
    { name: 'PostgreSQL', icon: 'üêò', color: 'bg-blue-100 dark:bg-blue-900/20' },
    { name: 'Docker', icon: 'üê≥', color: 'bg-blue-100 dark:bg-blue-900/20' },
    { name: 'AWS', icon: '‚òÅÔ∏è', color: 'bg-yellow-100 dark:bg-yellow-900/20' },
  ];

  return (
    <section id="skills" className="section-padding relative overflow-hidden section-divider">
      {/* Background with different blur and color scheme */}
      <div className="absolute inset-0 bg-gradient-to-br from-blue-50/60 via-indigo-50/80 to-purple-50/60 dark:from-dark-900/60 dark:via-dark-800/80 dark:to-dark-900/60 backdrop-blur-[3px]"></div>
      
      {/* Decorative background elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-20 left-1/4 w-96 h-96 bg-gradient-to-r from-indigo-400/15 to-purple-400/15 rounded-full blur-3xl float-animation"></div>
        <div className="absolute bottom-20 right-1/4 w-80 h-80 bg-gradient-to-r from-cyan-400/15 to-blue-400/15 rounded-full blur-3xl float-animation" style={{animationDelay: '2s'}}></div>
        <div className="absolute top-1/3 right-20 w-48 h-48 bg-gradient-to-r from-blue-400/12 to-indigo-400/12 rounded-full blur-2xl float-animation" style={{animationDelay: '4s'}}></div>
      </div>

      <div className="container-custom relative z-10">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <motion.h2
            className="text-4xl md:text-5xl font-bold text-dark-900 dark:text-white mb-6"
            initial={{ opacity: 0, y: 30 }}
            animate={inView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            My <span className="gradient-text">Skills</span>
          </motion.h2>
          <motion.p
            className="text-lg text-dark-600 dark:text-dark-400 max-w-3xl mx-auto"
            initial={{ opacity: 0, y: 30 }}
            animate={inView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            A comprehensive overview of my technical expertise and proficiency levels.
          </motion.p>
        </motion.div>

        {/* Skills Grid */}
        <div className="grid lg:grid-cols-3 gap-8 mb-16">
          {skillCategories.map((category, categoryIndex) => (
            <motion.div
              key={category.title}
              className="bg-white dark:bg-dark-700 rounded-xl p-6 shadow-sm hover:shadow-lg transition-all duration-300"
              initial={{ opacity: 0, y: 50 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.8, delay: 0.6 + categoryIndex * 0.2 }}
              whileHover={{ y: -5 }}
            >
              <h3 className="text-xl font-semibold text-dark-900 dark:text-white mb-6 text-center">
                {category.title}
              </h3>
              <div className="space-y-4">
                {category.skills.map((skill, skillIndex) => (
                  <motion.div
                    key={skill.name}
                    className="space-y-2"
                    initial={{ opacity: 0, x: -20 }}
                    animate={inView ? { opacity: 1, x: 0 } : {}}
                    transition={{ duration: 0.6, delay: 0.8 + categoryIndex * 0.2 + skillIndex * 0.1 }}
                  >
                    <div className="flex justify-between items-center">
                      <span className="text-sm font-medium text-dark-700 dark:text-dark-300">
                        {skill.name}
                      </span>
                      <span className="text-sm text-dark-500 dark:text-dark-400">
                        {skill.level}%
                      </span>
                    </div>
                    <div className="w-full bg-gray-200 dark:bg-dark-600 rounded-full h-2">
                      <motion.div
                        className={`h-2 rounded-full bg-gradient-to-r ${skill.color}`}
                        initial={{ width: 0 }}
                        animate={inView ? { width: `${skill.level}%` } : {}}
                        transition={{ duration: 1.5, delay: 1.0 + categoryIndex * 0.2 + skillIndex * 0.1 }}
                      />
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          ))}
        </div>

        {/* Technologies Grid */}
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 1.2 }}
          className="text-center"
        >
          <h3 className="text-2xl font-semibold text-dark-900 dark:text-white mb-8">
            Technologies I Work With
          </h3>
          <div className="grid grid-cols-2 sm:grid-cols-4 md:grid-cols-8 gap-4">
            {technologies.map((tech, index) => (
              <motion.div
                key={tech.name}
                className={`${tech.color} p-4 rounded-xl hover:shadow-lg transition-all duration-300 cursor-pointer`}
                whileHover={{ scale: 1.1, y: -5 }}
                whileTap={{ scale: 0.95 }}
                initial={{ opacity: 0, scale: 0.8 }}
                animate={inView ? { opacity: 1, scale: 1 } : {}}
                transition={{ duration: 0.6, delay: 1.4 + index * 0.1 }}
              >
                <div className="text-center">
                  <div className="text-2xl mb-2">{tech.icon}</div>
                  <div className="text-xs font-medium text-dark-700 dark:text-dark-300">
                    {tech.name}
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* Additional Skills */}
        <motion.div
          className="mt-16 text-center"
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 2.0 }}
        >
          <p className="text-lg text-dark-600 dark:text-dark-400 mb-6">
            Continuously expanding my skill set and staying up-to-date with the latest technologies.
          </p>
          <motion.button
            className="px-8 py-3 bg-primary-600/90 hover:bg-primary-700/90 text-white font-semibold rounded-full shadow-lg hover:shadow-xl transition-all duration-300 btn-blur"
            whileHover={{ scale: 1.05, y: -2 }}
            whileTap={{ scale: 0.95 }}
          >
            View Resume
          </motion.button>
        </motion.div>
      </div>
    </section>
  );
};

export default Skills;
