// src/components/Projects.js
import React, { useEffect, useState, useContext } from "react";
import ThemeContext from "../context/ThemeContext";

const Projects = () => {
  const [repos, setRepos] = useState([]);
  const { isDark } = useContext(ThemeContext);

  useEffect(() => {
    fetch("https://api.github.com/users/your-username/repos")
      .then((res) => {
        if (!res.ok) {
          throw new Error(`GitHub API error: ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        const list = Array.isArray(data) ? data : [];
        const filtered = list.filter((repo) => !repo.fork && !repo.private);
        setRepos(filtered);
      })
      .catch((err) => {
        console.error(err);
        setRepos([]);
      });
  }, []);

  return (
    <section id="projects"
      className={`py-10 transition-colors duration-500 ${
        isDark ? "bg-gray-900 text-white" : "bg-gray-100 text-gray-900"
      }`}
    >
      <div className="container mx-auto px-6">
        <h2 className="text-3xl font-bold mb-6 text-center">My Projects</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {repos.map((repo) => (
            <div
              key={repo.id}
              className={`p-5 rounded-2xl shadow-md transform transition hover:-translate-y-2 hover:shadow-xl ${
                isDark ? "bg-gray-800 text-gray-200" : "bg-white text-gray-900"
              }`}
            >
              <h3 className="text-xl font-semibold mb-2">{repo.name}</h3>
              <p className="text-sm mb-4">
                {repo.description ? repo.description : "No description"}
              </p>
              <a
                href={repo.html_url}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-500 hover:underline"
              >
                View on GitHub â†’
              </a>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
