import React from "react";
import { motion } from "framer-motion";
import { useInView } from "react-intersection-observer";

const Skills = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  // Define skills in card format
  const skills = [
    {
      name: "Java",
      description: "Built robust systems with OOP principles & exception handling",
      icon: "笘表

from-orange-500 to-red-500",
    },
    {
      name: "Spring Boot",
      description: "REST APIs, Dependency Injection, JPA, Microservices",
      icon: "沍ｱ",
      color: "from-green-500 to-emerald-500",
    },
    {
      name: "React.js",
      description: "SPA development with hooks, state mgmt, and component architecture",
      icon: "笞幢ｸ十

from-blue-500 to-cyan-500",
    },
    {
      name: "JavaScript",
      description: "DOM manipulation, ES6+, async programming, fetch APIs",
      icon: "沒彌

from-yellow-500 to-orange-500",
    },
    {
      name: "Tailwind CSS",
      description: "Utility-first styling for responsive and clean UIs",
      icon: "沁ｨ",
      color: "from-cyan-500 to-blue-500",
    },
  ];

  // (Reverted) Using simple per-card entrance animation without variants

  const iconHover = {
    rest: { rotate: 0, scale: 1 },
    hover: { rotate: 8, scale: 1.08, transition: { type: "spring", stiffness: 220, damping: 14 } },
  };

  const titleHover = {
    rest: { y: 0 },
    hover: { y: -2, transition: { type: "spring", stiffness: 220, damping: 18 } },
  };

  const descHover = {
    rest: { opacity: 0.85 },
    hover: { opacity: 1, transition: { duration: 0.2 } },
  };

  const glowHover = {
    rest: { opacity: 0, scale: 1 },
    hover: { opacity: 0.15, scale: 1.05, transition: { duration: 0.25 } },
  };

  return (
    <section
      id="skills"
      className="section-padding relative overflow-hidden bg-gradient-to-b from-white via-gray-50 to-gray-100 dark:from-dark-900 dark:via-dark-800 dark:to-dark-900"
    >
      <div className="container-custom relative z-10">
        {/* Heading */}
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-dark-900 dark:text-white mb-6">
            Skills
          </h2>
          <p className="text-lg text-dark-600 dark:text-dark-400 max-w-2xl mx-auto">
            Core competencies and technical expertise for building modern web applications
          </p>
        </motion.div>

        {/* Skills Grid (Card Layout) */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-8">
          {skills.map((skill, index) => (
            <motion.div
              key={skill.name}
              className="relative overflow-hidden bg-white dark:bg-dark-700 rounded-2xl shadow-md p-6 text-center transition-all duration-300 hover:shadow-xl"
              initial={{ opacity: 0, y: 30 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.6, delay: index * 0.15 }}
              whileHover="hover"
              onHoverStart={(e) => {
                e.currentTarget.style.transform = "translateY(-5px)";
              }}
              onHoverEnd={(e) => {
                e.currentTarget.style.transform = "translateY(0px)";
              }}
            >
              {/* Glow */}
              <motion.div
                className={`pointer-events-none absolute -inset-8 rounded-2xl bg-gradient-to-br ${skill.color} blur-2xl`}
                variants={glowHover}
                aria-hidden
              />

              {/* Icon */}
              <motion.div
                className={`relative w-16 h-16 mx-auto flex items-center justify-center rounded-xl bg-gradient-to-br ${skill.color} text-white text-3xl mb-4`}
                variants={iconHover}
              >
                {skill.icon}
              </motion.div>
              {/* Skill Title */}
              <motion.h3 className="text-lg font-semibold text-dark-900 dark:text-white mb-2" variants={titleHover}>
                {skill.name}
              </motion.h3>
              {/* Description */}
              <motion.p className="text-sm text-dark-600 dark:text-dark-400" variants={descHover}>
                {skill.description}
              </motion.p>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
