import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const Button = ({
  children,
  onClick,
  variant = 'primary',
  size = 'md',
  className = '',
  icon: Icon,
  iconPosition = 'right',
  hoverIcon: HoverIcon,
  ...props
}) => {
  const [isHovered, setIsHovered] = useState(false);

  const baseStyles = 'relative font-medium rounded-full transition-all duration-300 flex items-center justify-center gap-2 group';

  const sizeStyles = {
    sm: 'px-4 py-2 text-sm',
    md: 'px-6 py-2.5 text-base',
    lg: 'px-8 py-3.5 text-lg',
  };

  const variantStyles = {
    primary: 'bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-lg hover:shadow-xl',
    secondary: 'bg-white/80 dark:bg-gray-800/80 backdrop-blur-sm text-gray-800 dark:text-gray-200 shadow-sm hover:shadow-md',
    ghost: 'bg-transparent text-gray-700 dark:text-gray-300 hover:bg-gray-100/50 dark:hover:bg-gray-800/50',
  };

  const glowStyles = {
    primary: 'from-blue-500 to-purple-500',
    secondary: 'from-gray-200 to-gray-300 dark:from-gray-700 dark:to-gray-600',
    ghost: 'from-gray-200 to-gray-300 dark:from-gray-700 dark:to-gray-600',
  };

  return (
    <motion.div 
      className={`relative group ${className}`}
      whileHover={{ y: -3 }}
      whileTap={{ scale: 0.98 }}
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
    >
      <div 
        className={`absolute -inset-0.5 rounded-full opacity-0 group-hover:opacity-70 blur transition duration-500 group-hover:duration-200 ${glowStyles[variant]}`}
      />
      <button
        className={`${baseStyles} ${sizeStyles[size]} ${variantStyles[variant]}`}
        onClick={onClick}
        {...props}
      >
        {Icon && iconPosition === 'left' && (
          <Icon className={`w-5 h-5 ${isHovered && HoverIcon ? 'opacity-0' : 'opacity-100'}`} />
        )}
        <AnimatePresence mode="wait">
          <motion.span 
            key={isHovered && HoverIcon ? 'hover' : 'default'}
            initial={{ opacity: 0, x: 10 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -10 }}
            className="flex items-center gap-2"
          >
            {children}
          </motion.span>
        </AnimatePresence>
        {Icon && iconPosition === 'right' && (
          <Icon className={`w-5 h-5 ${isHovered && HoverIcon ? 'opacity-0' : 'opacity-100'}`} />
        )}
        {HoverIcon && isHovered && (
          <HoverIcon className="w-5 h-5 absolute right-3" />
        )}
      </button>
    </motion.div>
  );
};

export default Button;
