import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useInView } from "react-intersection-observer";
import { Code, Cpu, Database, Server, Smartphone, Palette, Layers, Terminal, Cloud } from "lucide-react";

const Skills = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  // Define skills in card format
  const skills = [
    {
      name: "Python",
      description: "Scripting, OOP, data processing, virtual environments",
      icon: "üêç",
      color: "from-yellow-400 to-blue-500",
    },
    {
      name: "Django",
      description: "High-level web framework, ORM, REST APIs, auth",
      icon: "üß©",
      color: "from-green-600 to-emerald-500",
    },
    {
      name: "React.js",
      description: "SPA development with hooks, state mgmt, and component architecture",
      icon: "‚öõÔ∏è",
      color: "from-blue-500 to-cyan-500",
    },
    {
      name: "JavaScript",
      description: "DOM manipulation, ES6+, async programming, fetch APIs",
      icon: "üìú",
      color: "from-yellow-500 to-orange-500",
    },
    {
      name: "Tailwind CSS",
      description: "Utility-first styling for responsive and clean UIs",
      icon: "üé®",
      color: "from-cyan-500 to-blue-500",
    },
  ];

  // Only glow effect on hover
  const glowVariants = {
    rest: { opacity: 0 },
    hover: { opacity: 0.18, transition: { duration: 0.2 } },
  };

  return (
    <section
      id="skills"
      className="section-padding relative overflow-hidden bg-gradient-to-b from-white via-gray-50 to-gray-100 dark:from-dark-900 dark:via-dark-800 dark:to-dark-900"
    >
      <div className="container-custom relative z-10">
        {/* Heading */}
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-dark-900 dark:text-white mb-6">
            Skills
          </h2>
          <p className="text-lg text-dark-600 dark:text-dark-400 max-w-2xl mx-auto">
            Core competencies and technical expertise for building modern web applications
          </p>
        </motion.div>

        {/* Skills Grid (Card Layout) */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-6 sm:gap-8">
          {skills.map((skill, index) => (
            <motion.div
              key={skill.name}
              initial={{ opacity: 0, y: 30 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.6, delay: index * 0.15 }}
            >
              <motion.div
                className="relative overflow-hidden bg-white dark:bg-dark-700 rounded-2xl shadow-md p-6 text-center transition-all duration-300 hover:shadow-xl min-h-[240px] h-full flex flex-col items-center justify-start"
                initial="rest"
                animate="rest"
                whileHover="hover"
              >
                {/* Glow */}
                <motion.div
                  className={`pointer-events-none absolute -inset-8 rounded-2xl bg-gradient-to-br ${skill.color} blur-2xl`}
                  variants={glowVariants}
                  aria-hidden
                />

                {/* Icon */}
                <div
                  className={`relative w-16 h-16 mx-auto flex items-center justify-center rounded-xl bg-gradient-to-br ${skill.color} text-white text-3xl mb-4`}
                >
                  {skill.icon}
                </div>
                {/* Skill Title */}
                <h3 className="text-lg font-semibold text-dark-900 dark:text-white mb-2">
                  {skill.name}
                </h3>
                {/* Description */}
                <p className="text-sm text-dark-600 dark:text-dark-400">
                  {skill.description}
                </p>
              </motion.div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
