import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { User, Code, Coffee, Target, Award, Briefcase, Lightbulb, GitBranch } from 'lucide-react';
import { useInView } from 'react-intersection-observer';
import { Tooltip } from 'react-tooltip';
import 'react-tooltip/dist/react-tooltip.css';

const About = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const [activeTab, setActiveTab] = useState('skills');
  const [hoveredSkill, setHoveredSkill] = useState(null);

  const skills = {
    frontend: [
      { name: 'React', level: 90, color: 'from-blue-400 to-blue-600' },
      { name: 'JavaScript', level: 85, color: 'from-yellow-400 to-yellow-600' },
      { name: 'HTML/CSS', level: 90, color: 'from-orange-400 to-orange-600' },
      { name: 'Tailwind CSS', level: 85, color: 'from-cyan-400 to-cyan-600' },
    ],
    backend: [
      { name: 'Node.js', level: 80, color: 'from-green-400 to-green-600' },
      { name: 'Python', level: 75, color: 'from-blue-400 to-blue-600' },
      { name: 'MongoDB', level: 70, color: 'from-green-500 to-green-700' },
      { name: 'SQL', level: 75, color: 'from-blue-400 to-blue-600' },
    ],
    tools: [
      { name: 'Git', level: 85, color: 'from-orange-400 to-orange-600' },
      { name: 'VS Code', level: 90, color: 'from-blue-400 to-blue-600' },
      { name: 'Figma', level: 70, color: 'from-purple-400 to-purple-600' },
      { name: 'Docker', level: 60, color: 'from-blue-400 to-blue-700' },
    ]
  };

  const experiences = [
    {
      role: 'Full Stack Developer',
      company: 'Self-Employed',
      period: '2023 - Present',
      description: 'Building personal projects and contributing to open source while continuously learning new technologies.',
      icon: <Briefcase className="w-5 h-5" />
    },
    {
      role: 'Coding Bootcamp',
      company: 'Self-Taught',
      period: '2022 - 2023',
      description: 'Completed various online courses and built projects to master full-stack development.',
      icon: <Award className="w-5 h-5" />
    }
  ];

  const features = [
    {
      icon: Code,
      title: 'Clean Code',
      description: 'Writing maintainable, readable, and efficient code that scales.',
      color: 'from-blue-500 to-cyan-500',
      delay: 0.2
    },
    {
      icon: Target,
      title: 'Problem Solver',
      description: 'Breaking down complex problems into elegant, simple solutions.',
      color: 'from-purple-500 to-pink-500',
      delay: 0.3
    },
    {
      icon: Lightbulb,
      title: 'Innovative Thinker',
      description: 'Bringing creative solutions to technical challenges.',
      color: 'from-yellow-500 to-orange-500',
      delay: 0.4
    },
    {
      icon: GitBranch,
      title: 'Version Control',
      description: 'Proficient in Git workflow and collaborative development.',
      color: 'from-green-500 to-teal-500',
      delay: 0.5
    }
  ];

  return (
    <section id="about" className="relative overflow-hidden py-20 lg:py-28">
      {/* Animated Background */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-40 -left-40 w-80 h-80 bg-gradient-to-r from-blue-500/10 to-purple-500/10 rounded-full blur-3xl animate-blob"></div>
        <div className="absolute -bottom-40 -right-40 w-96 h-96 bg-gradient-to-r from-pink-500/10 to-rose-500/10 rounded-full blur-3xl animate-blob animation-delay-2000"></div>
        <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-[600px] h-[600px] bg-gradient-to-r from-primary-500/5 to-cyan-500/5 rounded-full blur-3xl animate-pulse"></div>
      </div>

      {/* Grid Pattern Overlay */}
      <div className="absolute inset-0 bg-grid-white/10 dark:bg-grid-dark-800/10 [mask-image:linear-gradient(0deg,transparent,black_20%)]"></div>

      {/* Gradient Overlay */}
      <div className="absolute inset-0 bg-gradient-to-b from-white/80 via-transparent to-white/80 dark:from-dark-900/90 dark:via-dark-900/70 dark:to-dark-900/90"></div>

      <div className="container-custom relative z-10 px-4 sm:px-6">
        <div className="text-center mb-16">
          <motion.div
            ref={ref}
            initial={{ opacity: 0, y: 30 }}
            animate={inView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8 }}
          >
            <motion.span 
              className="inline-block text-sm font-medium text-primary-600 dark:text-primary-400 mb-4 px-4 py-1.5 rounded-full bg-primary-50 dark:bg-primary-900/30"
              initial={{ opacity: 0, y: 10 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.6, delay: 0.2 }}
            >
              Get to know me
            </motion.span>
            <motion.h2
              className="text-4xl md:text-5xl lg:text-6xl font-bold text-dark-900 dark:text-white mb-6"
              initial={{ opacity: 0, y: 20 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.8, delay: 0.3 }}
            >
              About <span className="bg-clip-text text-transparent bg-gradient-to-r from-primary-500 to-cyan-500">Me</span>
            </motion.h2>
            <motion.p
              className="text-lg md:text-xl text-dark-600 dark:text-dark-300 max-w-3xl mx-auto leading-relaxed"
              initial={{ opacity: 0, y: 20 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.8, delay: 0.4 }}
            >
              I'm a passionate developer who loves turning ideas into reality through clean, efficient code. 
              My journey in tech is driven by curiosity and a constant desire to learn and grow.
            </motion.p>
          </motion.div>
        </div>

        {/* Tabs Navigation */}
        <motion.div 
          className="flex flex-wrap justify-center gap-2 mb-12"
          initial={{ opacity: 0, y: 20 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6, delay: 0.4 }}
        >
          {['skills', 'experience', 'education'].map((tab) => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`px-6 py-2.5 rounded-full text-sm font-medium transition-all duration-300 ${
                activeTab === tab
                  ? 'bg-gradient-to-r from-primary-500 to-cyan-500 text-white shadow-lg shadow-primary-500/20'
                  : 'text-dark-600 dark:text-dark-300 hover:bg-gray-100 dark:hover:bg-dark-700'
              }`}
            >
              {tab.charAt(0).toUpperCase() + tab.slice(1)}
            </button>
          ))}
        </motion.div>

        {/* Tab Content */}
        <div className="max-w-5xl mx-auto">
          <AnimatePresence mode="wait">
            {activeTab === 'skills' && (
              <motion.div
                key="skills"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.4 }}
                className="grid md:grid-cols-2 gap-12"
              >
                {/* Left Column - About Text */}
                <motion.div
                  className="space-y-6"
                  initial={{ opacity: 0, x: -30 }}
                  animate={inView ? { opacity: 1, x: 0 } : {}}
                  transition={{ duration: 0.6, delay: 0.6 }}
                >
                  <h3 className="text-2xl md:text-3xl font-bold text-dark-900 dark:text-white">
                    Turning <span className="text-primary-500">Vision</span> Into Reality
                  </h3>
                  <div className="space-y-4 text-dark-600 dark:text-dark-300 leading-relaxed">
                    <p>
                      I'm a passionate full-stack developer with a keen eye for creating exceptional digital experiences. 
                      My journey in tech is driven by curiosity and a commitment to writing clean, efficient code.
                    </p>
                    <p>
                      I specialize in building responsive, accessible, and performant web applications using modern 
                      technologies and best practices. My goal is to create solutions that not only meet but exceed 
                      user expectations.
                    </p>
                  </div>

                  {/* Skills Overview */}
                  <div className="pt-4">
                    <h4 className="text-lg font-semibold text-dark-900 dark:text-white mb-4">What I Do</h4>
                    <div className="grid grid-cols-2 gap-4">
                      {features.map((feature, index) => (
                        <motion.div
                          key={feature.title}
                          className="p-4 bg-white dark:bg-dark-800 rounded-xl shadow-sm hover:shadow-lg transition-all duration-300 group"
                          initial={{ opacity: 0, y: 20 }}
                          animate={inView ? { opacity: 1, y: 0 } : {}}
                          transition={{ duration: 0.5, delay: 0.7 + index * 0.1 }}
                        >
                          <div className={`w-10 h-10 rounded-lg bg-gradient-to-r ${feature.color} flex items-center justify-center mb-3`}>
                            <feature.icon className="w-5 h-5 text-white" />
                          </div>
                          <h4 className="font-semibold text-dark-900 dark:text-white mb-1">{feature.title}</h4>
                          <p className="text-sm text-dark-500 dark:text-dark-400">{feature.description}</p>
                        </motion.div>
                      ))}
                    </div>
                  </div>
                </motion.div>

                {/* Right Column - Skills Progress */}
                <div className="space-y-8">
                  {Object.entries(skills).map(([category, skillList], catIndex) => (
                    <motion.div
                      key={category}
                      initial={{ opacity: 0, y: 20 }}
                      animate={inView ? { opacity: 1, y: 0 } : {}}
                      transition={{ duration: 0.5, delay: 0.5 + catIndex * 0.1 }}
                    >
                      <h4 className="text-lg font-semibold text-dark-900 dark:text-white mb-4 capitalize">
                        {category} Skills
                      </h4>
                      <div className="space-y-4">
                        {skillList.map((skill, skillIndex) => (
                          <div key={skill.name} className="relative">
                            <div className="flex justify-between text-sm mb-1.5">
                              <span className="font-medium text-dark-700 dark:text-dark-300">{skill.name}</span>
                              <span className="text-dark-500 dark:text-dark-400">{skill.level}%</span>
                            </div>
                            <div 
                              className="h-2.5 bg-gray-200 dark:bg-dark-700 rounded-full overflow-hidden"
                              onMouseEnter={() => setHoveredSkill(skill.name)}
                              onMouseLeave={() => setHoveredSkill(null)}
                              data-tooltip-id="skill-tooltip"
                              data-tooltip-content={`${skill.level}% proficiency in ${skill.name}`}
                            >
                              <motion.div
                                className={`h-full rounded-full bg-gradient-to-r ${skill.color} relative`}
                                initial={{ width: 0 }}
                                animate={{ width: `${skill.level}%` }}
                                transition={{ duration: 1, delay: 0.5 + (catIndex * 0.1) + (skillIndex * 0.05) }}
                              >
                                <div className={`absolute inset-0 bg-white/20 ${hoveredSkill === skill.name ? 'opacity-100' : 'opacity-0'} transition-opacity duration-300`}></div>
                              </motion.div>
                            </div>
                          </div>
                        ))}
                      </div>
                    </motion.div>
                  ))}
                </div>
              </motion.div>
            )}

            {activeTab === 'experience' && (
              <motion.div
                key="experience"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.4 }}
                className="max-w-3xl mx-auto"
              >
                <h3 className="text-2xl md:text-3xl font-bold text-center text-dark-900 dark:text-white mb-12">
                  My <span className="text-primary-500">Experience</span>
                </h3>
                <div className="space-y-8">
                  {experiences.map((exp, index) => (
                    <motion.div
                      key={exp.role}
                      className="relative pl-8 border-l-2 border-gray-200 dark:border-dark-700 group"
                      initial={{ opacity: 0, x: -20 }}
                      animate={inView ? { opacity: 1, x: 0 } : {}}
                      transition={{ duration: 0.5, delay: 0.2 + index * 0.15 }}
                    >
                      <div className="absolute -left-2.5 top-0 w-4 h-4 rounded-full bg-gradient-to-r from-primary-500 to-cyan-500 border-4 border-white dark:border-dark-900"></div>
                      <div className="absolute -left-8 top-0 w-8 h-8 rounded-full bg-primary-500/10 flex items-center justify-center text-primary-500 group-hover:bg-primary-500/20 transition-colors duration-300">
                        {exp.icon}
                      </div>
                      <div className="bg-white dark:bg-dark-800 rounded-xl p-6 shadow-sm hover:shadow-lg transition-all duration-300">
                        <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-2 mb-2">
                          <h4 className="text-lg font-semibold text-dark-900 dark:text-white">{exp.role}</h4>
                          <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-primary-100 text-primary-800 dark:bg-primary-900/30 dark:text-primary-300">
                            {exp.period}
                          </span>
                        </div>
                        <div className="text-primary-600 dark:text-primary-400 font-medium mb-3">{exp.company}</div>
                        <p className="text-dark-600 dark:text-dark-300">{exp.description}</p>
                      </div>
                    </motion.div>
                  ))}
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        {/* CTA Section */}
        <motion.div
          className="mt-20 text-center relative z-10"
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.6 }}
        >
          <div className="relative inline-block w-full max-w-3xl">
            <div className="absolute -inset-1 bg-gradient-to-r from-primary-500 to-cyan-500 rounded-2xl opacity-20 blur-lg"></div>
            <div className="relative bg-gradient-to-r from-white to-white/90 dark:from-dark-800 dark:to-dark-800/90 p-8 rounded-2xl shadow-xl">
              <h3 className="text-2xl md:text-3xl font-bold text-dark-900 dark:text-white mb-4">
                Let's Build Something <span className="text-primary-500">Amazing</span> Together
              </h3>
              <p className="text-lg text-dark-600 dark:text-dark-300 mb-6">
                Have a project in mind or want to collaborate? I'd love to hear about it!
              </p>
              <motion.a
                href="#contact"
                className="inline-flex items-center px-8 py-3.5 bg-gradient-to-r from-primary-500 to-cyan-500 text-white font-medium rounded-full shadow-lg hover:shadow-xl hover:shadow-primary-500/20 transition-all duration-300 group"
                whileHover={{ y: -3, scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
              >
                <span>Get In Touch</span>
                <svg 
                  className="w-4 h-4 ml-2 group-hover:translate-x-1 transition-transform duration-300" 
                  fill="none" 
                  viewBox="0 0 24 24" 
                  stroke="currentColor"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 5l7 7m0 0l-7 7m7-7H3" />
                </svg>
              </motion.a>
            </div>
          </div>
        </motion.div>
      </div>
      <Tooltip id="skill-tooltip" place="top" effect="solid" className="z-50" />
    </section>
  );
};

export default About;
