import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Code, Github, Linkedin, Mail, ArrowUp, MessageCircle, Coffee } from 'lucide-react';

const Wave = () => (
  <div className="absolute top-0 left-0 w-full overflow-hidden leading-[0]">
    <svg
      className="w-full h-16"
      viewBox="0 0 1200 120"
      preserveAspectRatio="none"
      xmlns="http://www.w3.org/2000/svg"
    >
      <path
        d="M321.39 56.44c58-10.79 114.16-30.13 172-41.86 82.39-16.72 168.19-17.73 250.45-.39C823.78 31 906.67 72 985.66 92.83c70.05 18.48 146.53 26.09 214.34 3V0H0v27.35a600.21 600.21 0 00321.39 29.09z"
        className="fill-current text-primary-900/10 dark:text-primary-900/20"
      />
    </svg>
  </div>
);

const Footer = () => {
  const [showScroll, setShowScroll] = useState(false);
  const [hoveredLink, setHoveredLink] = useState(null);
  const currentYear = new Date().getFullYear();

  const socialLinks = [
    { 
      icon: Github, 
      href: 'https://github.com/TheOnlyBaddy', 
      label: 'GitHub',
      color: 'text-gray-800 dark:text-gray-200',
      hover: 'hover:bg-gray-100 dark:hover:bg-gray-800',
      iconColor: 'text-gray-700 dark:text-gray-300'
    },
    { 
      icon: Linkedin, 
      href: 'https://www.linkedin.com/in/shubh-barnwal', 
      label: 'LinkedIn',
      color: 'text-blue-600 dark:text-blue-400',
      hover: 'hover:bg-blue-50 dark:hover:bg-blue-900/20',
      iconColor: 'text-blue-600 dark:text-blue-400'
    },
    { 
      icon: Mail, 
      href: 'mailto:barnwalshubh8002434@gmail.com', 
      label: 'Email',
      color: 'text-red-500 dark:text-red-400',
      hover: 'hover:bg-red-50 dark:hover:bg-red-900/20',
      iconColor: 'text-red-500 dark:text-red-400'
    },
  ];

  const quickLinks = ['Home', 'About', 'Skills', 'Projects', 'Contact'];

  useEffect(() => {
    const checkScroll = () => {
      if (!showScroll && window.pageYOffset > 400) {
        setShowScroll(true);
      } else if (showScroll && window.pageYOffset <= 400) {
        setShowScroll(false);
      }
    };
    window.addEventListener('scroll', checkScroll);
    return () => window.removeEventListener('scroll', checkScroll);
  }, [showScroll]);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <footer className="relative bg-gradient-to-b from-dark-900 to-dark-800 dark:from-black dark:to-dark-950 text-white pt-20 pb-12 overflow-hidden">
      {/* Animated Wave */}
      <Wave />

      {/* Floating Elements */}
      <div className="absolute inset-0 overflow-hidden opacity-10">
        {[...Array(8)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute rounded-full bg-primary-400"
            style={{
              width: Math.random() * 10 + 5,
              height: Math.random() * 10 + 5,
              top: `${Math.random() * 100}%`,
              left: `${Math.random() * 100}%`,
            }}
            animate={{
              y: [0, -20, 0],
              opacity: [0.3, 0.7, 0.3],
            }}
            transition={{
              duration: Math.random() * 5 + 5,
              repeat: Infinity,
              ease: 'easeInOut',
              delay: Math.random() * 2,
            }}
          />
        ))}
      </div>

      <div className="container-custom relative z-10">
        <div className="grid md:grid-cols-3 gap-12 items-start">
          {/* Logo & Description */}
          <motion.div
            className="text-center md:text-left"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <div className="flex items-center justify-center md:justify-start space-x-3 mb-6">
              <motion.div 
                className="p-2 bg-primary-500/10 rounded-lg"
                whileHover={{ scale: 1.1, rotate: 10 }}
              >
                <Code className="h-8 w-8 text-primary-400" />
              </motion.div>
              <span className="text-2xl font-bold bg-gradient-to-r from-primary-400 to-cyan-400 bg-clip-text text-transparent">
                DevPortfolio
              </span>
            </div>
            <p className="text-gray-400 text-sm leading-relaxed mb-6">
              Crafting digital experiences with clean code and innovative solutions. 
              Let's build something amazing together.
            </p>
            <div className="flex space-x-4 justify-center md:justify-start">
              {socialLinks.map((social, index) => (
                <motion.a
                  key={social.label}
                  href={social.href}
                  target="_blank"
                  rel="noopener noreferrer"
                  className={`p-2 rounded-full ${social.hover} transition-all duration-300`}
                  whileHover={{ y: -3, scale: 1.1 }}
                  whileTap={{ scale: 0.95 }}
                  onMouseEnter={() => setHoveredLink(social.label)}
                  onMouseLeave={() => setHoveredLink(null)}
                >
                  <social.icon className={`h-5 w-5 ${social.iconColor}`} />
                </motion.a>
              ))}
            </div>
          </motion.div>

          {/* Quick Links */}
          <motion.div
            className="text-center md:text-left"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            <h3 className="text-lg font-semibold mb-6 bg-gradient-to-r from-primary-400 to-cyan-400 bg-clip-text text-transparent inline-block">
              Quick Links
            </h3>
            <div className="space-y-3">
              {quickLinks.map((link) => (
                <motion.div
                  key={link}
                  whileHover={{ x: 5 }}
                  transition={{ type: 'spring', stiffness: 300 }}
                >
                  <a
                    href={`#${link.toLowerCase()}`}
                    className={`block text-gray-400 hover:text-primary-400 transition-colors duration-300 text-sm group`}
                  >
                    {link}
                  </a>
                </motion.div>
              ))}
            </div>
          </motion.div>
        </div>

        {/* Divider */}
        <motion.div
          className="border-t border-dark-700 mt-8 pt-8"
          initial={{ opacity: 0, scaleX: 0 }}
          whileInView={{ opacity: 1, scaleX: 1 }}
          transition={{ duration: 0.8, delay: 0.6 }}
          viewport={{ once: true }}
        />

        {/* Bottom Section */}
        <div className="flex flex-col md:flex-row justify-between items-center space-y-4 md:space-y-0">
          <motion.p
            className="text-gray-400 text-sm"
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ duration: 0.6, delay: 0.8 }}
            viewport={{ once: true }}
          >
            Â© {currentYear} Shubh Barnnwal. All rights reserved.
          </motion.p>

          <motion.div
            className="flex space-x-6 text-sm text-gray-400"
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ duration: 0.6, delay: 1.0 }}
            viewport={{ once: true }}
          >
            <a href="#" className="hover:text-primary-400 transition-colors duration-300">
              Privacy Policy
            </a>
            <a href="#" className="hover:text-primary-400 transition-colors duration-300">
              Terms of Service
            </a>
          </motion.div>
        </div>

        {/* Back to Top Button */}
        <motion.div
          className="text-center mt-8"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 1.2 }}
          viewport={{ once: true }}
        >
          <motion.button
            onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
            className="px-6 py-2 border border-primary-400 text-primary-400 hover:bg-primary-400 hover:text-dark-900 rounded-lg transition-all duration-300 text-sm font-medium"
            whileHover={{ scale: 1.05, y: -2 }}
            whileTap={{ scale: 0.95 }}
          >
            Back to Top
          </motion.button>
        </motion.div>
      </div>
    </footer>
  );
};

export default Footer;
