import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import About from './components/About';
import Skills from './components/Skills';
import Projects from './components/Projects';
import Contact from './components/Contact';
import Footer from './components/Footer';
import ThemeContext from './context/ThemeContext';

function App() {
  const [isDark, setIsDark] = useState(() => {
    const saved = localStorage.getItem('darkMode');
    return saved ? JSON.parse(saved) : false;
  });

  useEffect(() => {
    localStorage.setItem('darkMode', JSON.stringify(isDark));
    if (isDark) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [isDark]);

  const toggleTheme = () => {
    setIsDark(!isDark);
  };

  return (
    <ThemeContext.Provider value={{ isDark, toggleTheme }}>
      <div className="min-h-screen">
        {/* Global Background Elements */}
        <div className="fixed inset-0 bg-grid-pattern bg-grid opacity-[0.02] dark:opacity-[0.03] pointer-events-none"></div>

        {/* Floating Elements for Subtle 3D Effect */}
        <div className="fixed inset-0 overflow-hidden pointer-events-none">
          <motion.div
            className="absolute top-20 left-10 w-32 h-32 bg-gradient-to-br from-primary-200/20 to-transparent rounded-full blur-xl"
            animate={{ 
              x: [0, 20, 0],
              y: [0, -20, 0],
            }}
            transition={{
              duration: 20,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
          <motion.div
            className="absolute top-40 right-20 w-24 h-24 bg-gradient-to-br from-blue-200/20 to-transparent rounded-full blur-xl"
            animate={{ 
              x: [0, -15, 0],
              y: [0, 15, 0],
            }}
            transition={{
              duration: 25,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
          <motion.div
            className="absolute bottom-40 left-1/4 w-20 h-20 bg-gradient-to-br from-purple-200/20 to-transparent rounded-full blur-xl"
            animate={{ 
              x: [0, 10, 0],
              y: [0, -10, 0],
            }}
            transition={{
              duration: 30,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
        </div>

        <Navbar />

        <main className="relative z-10">
          {/* Hero Section */}
          <section className="relative overflow-hidden bg-gradient-to-br from-blue-50 via-white to-purple-50 dark:from-dark-900 dark:via-dark-800 dark:to-dark-900">
            <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,')] opacity-5 dark:opacity-10"></div>
            <Hero />
          </section>

          {/* About Section */}
          <section className="relative overflow-hidden bg-gradient-to-br from-amber-50 via-orange-50 to-amber-100 dark:from-amber-900/30 dark:via-amber-800/20 dark:to-amber-900/30">
            <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,')] opacity-40 dark:opacity-20"></div>
            <div className="absolute inset-0 bg-gradient-to-br from-transparent via-amber-100/30 to-transparent dark:from-transparent dark:via-amber-900/10 dark:to-transparent"></div>
            <About />
          </section>

          {/* Skills Section */}
          <section className="relative overflow-hidden bg-gradient-to-br from-indigo-50 via-blue-50 to-violet-50 dark:from-slate-900 dark:via-indigo-900/50 dark:to-slate-900">
            <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,')] opacity-30 dark:opacity-20"></div>
            <div className="absolute inset-0 bg-[radial-gradient(circle_at_center,_var(--tw-gradient-stops))] from-transparent via-white/40 to-transparent dark:from-transparent dark:via-indigo-900/10 dark:to-transparent"></div>
            <Skills />
          </section>

          {/* Projects Section */}
          <section className="relative overflow-hidden bg-gradient-to-br from-amber-50 via-orange-50 to-amber-100 dark:from-amber-900/30 dark:via-amber-800/20 dark:to-amber-900/30">
            <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,')] opacity-40 dark:opacity-20"></div>
            <Projects />
          </section>

          {/* Contact Section */}
          <section className="relative overflow-hidden bg-gradient-to-br from-blue-50 via-white to-purple-50 dark:from-dark-900 dark:via-dark-800 dark:to-dark-900">
            <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,')] opacity-5 dark:opacity-10"></div>
            <Contact />
          </section>
        </main>

        <Footer />
      </div>
    </ThemeContext.Provider>
  );
}

export default App;
